#!/usr/bin/env python

def count_floors(data):
    count = 0
    for item in data:
        if item == '(':
            count += 1
        elif item == ')':
            count -= 1
    return count

def at_floor(data, position):
    count = 0
    for n in range(len(data)):
        if data[n] == '(':
            count += 1
        elif data[n] == ')':
            count -= 1
        if count == position:
            return n + 1

def test1():
    assert count_floors('(())') == 0
    assert count_floors('()()') == 0
    assert count_floors('(((') == 3
    assert count_floors('(()(()(') == 3
    assert count_floors('))(((((') == 3

def test2():
    assert at_floor(')', -1) == 1
    assert at_floor('()())', -1) == 5

