#!/usr/bin/env python

class FFT:
    def __init__(self, input):
        self.input = input
        self.phase = [int(n) for n in list(input)]

    def get_pattern(self, num, iteration):
        pattern0 = [0, 1, 0, -1]
        value = (num + 1) // (iteration + 1)
        return pattern0[value % 4]

    def apply_iteration(self, input, iteration):
        result = 0
        for n in range(len(input)):
            result += input[n] * self.get_pattern(n, iteration)
        return abs(result) % 10

    def run_phase(self):
        result = []
        phase = self.phase
        self.phase = []
        for iteration in range(len(phase)):
            if iteration % 10 == 0:
                print(f"{len(phase)} {iteration}")
            result += [self.apply_iteration(phase, iteration)]
        self.phase += result
        return result

    def run(self, phases=100, start=0):
        for n in range(phases):
            print(n)
            self.run_phase()
        answer = self.phase
        return ''.join([str(answer[n]) for n in range(start, 8)])

    def run2(self, phases=100):
        h = self.input
        i = (h[int(h[0:7]):])
        for a in range(phases):
            print(a)
            string = ''
            e = 0
            while e < len(i):
                if e == 0:
                    total = 0
                    for f in i:
                        total += int(f)
                elif e > 0:
                    total -= int(i[e-1])
                string += str(total)[-1]
                e+=1
            i = string
        return  i[0:8]

def test_1():
    fft = FFT('12345678')
    assert fft.phase == [1, 2, 3, 4, 5, 6, 7, 8]
    assert fft.get_pattern(0, 1) == 0
    assert fft.get_pattern(1, 1) == 1
    assert fft.get_pattern(2, 1) == 1
    assert fft.get_pattern(3, 1) == 0
    fft.run_phase()
    assert fft.phase == [4, 8, 2, 2, 6, 1, 5, 8]
    fft.run_phase()
    assert fft.phase == [3, 4, 0, 4, 0, 4, 3, 8]
    fft.run_phase()
    assert fft.phase == [0, 3, 4, 1, 5, 5, 1, 8]
    fft.run_phase()
    assert fft.phase == [0, 1, 0, 2, 9, 4, 9, 8]

def test_2():
    fft = FFT('80871224585914546619083218645595')
    assert fft.run(100) == '24176176'

    fft = FFT('19617804207202209144916044189917')
    assert fft.run(100) == '73745418'

    fft = FFT('69317163492948606335995924319873')
    assert fft.run(100) == '52432133'

def test_part1():
    fft = FFT('')
    assert fft.run(100) ==  '25131128'

if __name__ == '__main__':
    fft = FFT('')
    print(f"part 1: {fft.run(100)}")
    fft = FFT(''*10000)
    print(f"part 2: {fft.run2(100)}")
